local familyNameLen = string.len(a[1]);
local firstName1Len = string.len(a[2]);
local firstName2Len = string.len(a[3]);

local function getRandomNameSub(str,randomNum)
	local startIdx = randomNum > 0 and string.find(str, ",", randomNum) or 0;
	local endIdx = string.find(str, ",", startIdx+1) or 0;
	return string.sub(str, startIdx+1, endIdx-1);
end

local function getRandomFamilyName()
	local randomNum = math.random(0,familyNameLen);
	return getRandomNameSub(a[1],randomNum);
end

local function getRandomFirstName()
	local firstName1,firstName2 = "","";
	local randomNum = math.random(0,firstName1Len);
	local checkType = randomNum % 3;
	if checkType <= 1 then
		firstName1 = getRandomNameSub(a[2],randomNum);
	end
	if checkType >= 1 then
		local randomNum2 = math.random(0,firstName2Len);
		firstName2 = getRandomNameSub(a[3],randomNum2);
	end
	return firstName1 .. firstName2;
end

local function getRandomName()
	local familyName = getRandomFamilyName();
	local firstName = getRandomFirstName();
	return familyName .. firstName;
end

